$_MODULE['<{comnpay}prestashop>admin_afcc703a1edfb8184a80cf9d71c9c62b'] = 'Retrouvez l\'ensemble des paramètres de ComNpay sur votre espace personnel Afone Paiement.';
$_MODULE['<{comnpay}prestashop>admin_33a55de9971e6c56e8ea4a3cf5409a30'] = 'Vous pourrez personnaliser votre page de paiement, configurer les seuils de déclenchement du 3D Secure, adapter la gestion du risque à votre activité…';
$_MODULE['<{comnpay}prestashop>admin_41cda55d9c5f7c68829ad4d477dd23c7'] = 'Quels sont les moyens de paiement acceptés par ComNpay ?';
$_MODULE['<{comnpay}prestashop>admin_36de9ba19365197999e88da6d08ea072'] = 'ComNpay permet l\'encaissement des cartes bancaires les plus représentées en France et à l\'international : CB, Visa, MasterCard et American Express.';
$_MODULE['<{comnpay}prestashop>admin_0396574364d3f674143ce8c8432e872c'] = 'Qui sommes-nous ?';
$_MODULE['<{comnpay}prestashop>admin_181d6c8c56ef079f0fcafdc952b3bca5'] = 'ComNpay est la solution pour les paiements en ligne de l\'établissement de paiement Afone Paiement, agréé en 2011 par l\'Autorité de Contrôle Prudentiel (ACPR) de la Banque de France. ComNpay propose des solutions d\'encaissements des cartes bancaires en France et à l\'étranger et dispose des passeports pour exercer son activité dans l\'ensemble des pays de l\'espace Sepa.';
$_MODULE['<{comnpay}prestashop>admin_e78fa45297d9ff9299cfa5cba9447232'] = 'Vous pouvez nous envoyer vos questions et vos suggestions sur ce formulaire : ';
